(2752, 221, '_content_1_name', 'field_67bdc0c25fa50'),
(2753, 221, 'content_1_text', '<h2 class=\"section-head__title\">„Objekat je projektovan tako da se uklapa u planinski ambijent dok istovremeno pruža prepoznatljiv i moderan izgled.“</h2>'),
(2754, 221, '_content_1_text', 'field_67bdc0cc5fa51'),
(2755, 221, 'content_1_position', ' mast.inž. arh. / Project manager'),
(2756, 221, '_content_1_position', 'field_67bdc272896bc'),
(2757, 221, 'content_2_panels_0_text', '„Osmišljene su zidne obloge sa integrisanom led rasvetom, koje dodaju teksturu i dubinu spavaćim sobama, pogotovo uveče kada emituju i prijatno difuzno svetlo doprinoseći atmosferi topline.“'),
(2758, 221, '_content_2_panels_0_text', 'field_67bdbce21a242'),
(2759, 221, 'content_2_panels_0_image', '213'),
(2760, 221, '_content_2_panels_0_image', 'field_67bdbce81a243'),
(2761, 221, 'content_2_panels_1_text', '„Praktična rešenja za odlaganje su neprimetno integrisana, pružajući funkcionalnost bez ugrožavanja dizajna.“'),
(2762, 221, '_content_2_panels_1_text', 'field_67bdbce21a242'),
(2763, 221, 'content_2_panels_1_image', '214'),
(2764, 221, '_content_2_panels_1_image', 'field_67bdbce81a243'),
(2765, 221, 'content_2_panels_2_text', '„Prostorne tokove smo formirali tako da svaki deo objekta bude lako dostupan i intuitivno povezan sa ključnim sadržajima- od skijašnice i garaže u podrumu objekta, do spa centra i bazena na poslednjem spratu objekta.“'),
