(3606, 257, '_content_3_accordion_title', 'field_67bdd8ea50d53'),
(3607, 257, 'content_3_accordion_items_0_question', 'Šta je uključeno u cenu'),
(3608, 257, '_content_3_accordion_items_0_question', 'field_67bdd8ff50d55'),
(3609, 257, 'content_3_accordion_items_0_answer', 'Apartmani se prodaju potpuno opremljeni. Kupovinom apartmana stičete pravo na pogodnosti prilikom korišćenja sadržaja hotela. Čuvanje skija u skijašnici je besplatno za vlasnike apartmana u naredne dve godine od dana kupovine apartmana.'),
(3610, 257, '_content_3_accordion_items_0_answer', 'field_67bdd92b50d56'),
(3611, 257, 'content_3_accordion_items_1_question', 'Opremanje apartmana'),
(3612, 257, '_content_3_accordion_items_1_question', 'field_67bdd8ff50d55'),
(3613, 257, 'content_3_accordion_items_1_answer', 'Svi apartmani su opremljeni ugradnim i komadnim nameštajem, tehnikom i sitnim inventarom prema specifikaciji koja je dostupna na uvid prilikom prezentacije apartmana.'),
(3614, 257, '_content_3_accordion_items_1_answer', 'field_67bdd92b50d56'),
(3615, 257, 'content_3_accordion_items_2_question', 'Parking'),
(3616, 257, '_content_3_accordion_items_2_question', 'field_67bdd8ff50d55'),
(3617, 257, 'content_3_accordion_items_2_answer', 'EMR ima ukupno 76 garažnih mesta u suterenu, 32 garažna mesta u I fazi i 44 u II fazi.'),
(3618, 257, '_content_3_accordion_items_2_answer', 'field_67bdd92b50d56'),
(3619, 257, 'content_3_accordion_items_3_question', 'Sadržaji'),
(3620, 257, '_content_3_accordion_items_3_question', 'field_67bdd8ff50d55'),
(3621, 257, 'content_3_accordion_items_3_answer', 'Hotel raspolaže spa centrom od 1000m2  sa bazenom, saunom, prostorijama za masažu, teretanom i salom za rekreaciju. U okviru hotela se nalazi i fleksibilna konferencijska sala. sala za sastanke, dečija igraonica, ski bar i restoran.'),
(3622, 257, '_content_3_accordion_items_3_answer', 'field_67bdd92b50d56'),
(3623, 257, 'content_3_accordion_items_4_question', 'Odricanja od odgovornosti'),
(3624, 257, '_content_3_accordion_items_4_question', 'field_67bdd8ff50d55'),
(3625, 257, 'content_3_accordion_items_4_answer', 'Informacije koje se nalaze na ovoj veb stranici zasnovane su na trenutnim planskim dokumentima i trenutno važećim odredbama zakona. Fotorealistični prikazi zgrade imaju za cilj da pruže utisak o planiranoj strukturi i nisu obavezujući u pogledu korišćenih materijala, boja, okova, strukturalnog dizajna, okolnih područja ili pejzaža. Emerald Mountain Resort zadržava isključivo pravo na izmene.'),
