(3757, 258, 'content_2_accordion_title', 'Informacije za kupce'),
(3758, 258, '_content_2_accordion_title', 'field_67bdd8ea50d53'),
(3759, 258, 'content_2_accordion_items_0_question', 'Šta je uključeno u cenu'),
(3760, 258, '_content_2_accordion_items_0_question', 'field_67bdd8ff50d55'),
(3761, 258, 'content_2_accordion_items_0_answer', 'Apartmani se prodaju potpuno opremljeni. Kupovinom apartmana stičete pravo na pogodnosti prilikom korišćenja sadržaja hotela. Čuvanje skija u skijašnici je besplatno za vlasnike apartmana u naredne dve godine od dana kupovine apartmana.'),
(3762, 258, '_content_2_accordion_items_0_answer', 'field_67bdd92b50d56'),
(3763, 258, 'content_2_accordion_items_1_question', 'Opremanje apartmana'),
(3764, 258, '_content_2_accordion_items_1_question', 'field_67bdd8ff50d55'),
(3765, 258, 'content_2_accordion_items_1_answer', 'Svi apartmani su opremljeni ugradnim i komadnim nameštajem, tehnikom i sitnim inventarom prema specifikaciji koja je dostupna na uvid prilikom prezentacije apartmana.'),
(3766, 258, '_content_2_accordion_items_1_answer', 'field_67bdd92b50d56'),
(3767, 258, 'content_2_accordion_items_2_question', 'Parking'),
(3768, 258, '_content_2_accordion_items_2_question', 'field_67bdd8ff50d55'),
(3769, 258, 'content_2_accordion_items_2_answer', 'EMR ima ukupno 76 garažnih mesta u suterenu, 32 garažna mesta u I fazi i 44 u II fazi.'),
(3770, 258, '_content_2_accordion_items_2_answer', 'field_67bdd92b50d56'),
(3771, 258, 'content_2_accordion_items_3_question', 'Sadržaji'),
(3772, 258, '_content_2_accordion_items_3_question', 'field_67bdd8ff50d55'),
(3773, 258, 'content_2_accordion_items_3_answer', 'Hotel raspolaže spa centrom od 1000m2  sa bazenom, saunom, prostorijama za masažu, teretanom i salom za rekreaciju. U okviru hotela se nalazi i fleksibilna konferencijska sala. sala za sastanke, dečija igraonica, ski bar i restoran.'),
(3774, 258, '_content_2_accordion_items_3_answer', 'field_67bdd92b50d56'),
(3775, 258, 'content_2_accordion_items_4_question', 'Odricanja od odgovornosti'),
(3776, 258, '_content_2_accordion_items_4_question', 'field_67bdd8ff50d55'),
(3777, 258, 'content_2_accordion_items_4_answer', 'Informacije koje se nalaze na ovoj veb stranici zasnovane su na trenutnim planskim dokumentima i trenutno važećim odredbama zakona. Fotorealistični prikazi zgrade imaju za cilj da pruže utisak o planiranoj strukturi i nisu obavezujući u pogledu korišćenih materijala, boja, okova, strukturalnog dizajna, okolnih područja ili pejzaža. Emerald Mountain Resort zadržava isključivo pravo na izmene.'),
(3778, 258, '_content_2_accordion_items_4_answer', 'field_67bdd92b50d56'),
